package com.knowledge.music.lottery.api.controllers;import com.knowledge.music.lottery.api.model.shazam.recommend.RecommendResponse;import com.knowledge.music.lottery.api.model.SongSearchRequest;import com.knowledge.music.lottery.api.services.RecommendationService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("/music")public class LotteryController {    @Autowired    private RecommendationService recommendationService;    @PostMapping("/get-recommended")    public RecommendResponse getRecommendedMusic(@RequestBody SongSearchRequest songSearchRequest) {        return recommendationService.getRecommendedSongs(songSearchRequest);    }}